mapper.select:
SELECT `id`,`releaseId`,`statusId`,`refusedMessage`,`createTime`,`updateTime`,`sfId`,`orderId` FROM `tb_distribution`


        and id = #{id}


        and releaseId = #{releaseId}


        and statusId = #{statusId}


        and refusedMessage = #{refusedMessage}


        and sfId = #{sfId}


        and orderId = #{orderId}
